(data: IData[], key: keyof IData, count = 5) => {
  const values = [
    ...new Set(
      data.map((item) => {
        if (key === 'age') {
          return Math.floor(item[key] / 10) * 10;
        }
        return item[key].toString().split(' ')[0];
      })
    ),
  ];
  return values.slice(0, count).map((value) => ({ text: value, value }));
}

